var Path, arg, conf, i, j, len, ref;

Path = require('path');

conf = {};

conf.live = process.env.NODE_ENV === 'production';

conf.name = Path.basename(Path.dirname(process.argv[1]));

conf.proto = 'http';

conf.host = 'localhost';

conf.port = 9000;

conf.url = conf.proto + "://" + conf.host + ":" + conf.port;

conf.charset = 'utf-8';

conf.error = false;

conf.log = 'trace';

ref = process.argv.slice(2);
for (i = j = 0, len = ref.length; j < len; i = ++j) {
  arg = ref[i];
  if (arg.slice(0, 2) !== '--') {
    continue;
  }
  arg = arg.slice(2).split('=');
  conf[arg[0]] = arg[1] || true;
}

module.exports = conf;
