var util;

util = {};

util.bytes = function(bytes) {
  var i, sz;
  sz = ['B', 'KB', 'MB', 'GB', 'TB'];
  if (!bytes) {
    return 0;
  }
  i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
  return Math.round(bytes / Math.pow(1024, i), 2) + sz[i];
};

util.addCommas = function(str) {
  str = String(str).replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  return str;
};

module.exports = util;
